	<div id="files">			</div>	<script type="text/javascript">	$(document).ready(function() {		var files = <?php echo json_encode($files); ?>;		var file_tree = build_file_tree(files);		file_tree.appendTo('#files');				function build_file_tree(files) {						var tree = $('<ul>');						for (x in files) {								if (typeof files[x] == "object") {					var span = $('<span>').html(x).appendTo(						$('<li>').appendTo(tree).addClass('folder')					);					var subtree = build_file_tree(files[x]).hide();					span.after(subtree);					span.click(function() {						$(this).parent().find('ul:first').toggle();					});									} else {					$('<li>').html(files[x]).appendTo(tree).addClass('file');				}							}						return tree;					}	});		</script>